$dbs = @("M3STG09ST01EA","M3STG09ST01EH","M3STG09ST01FND","M3STG09ST01Grid","M3STG09ST01IEC","M3STG09ST01MDP","M3STG09ST01PS","M3STG09ST02EA","M3STG09ST02EH","M3STG09ST02FND","M3STG09ST02Grid","M3STG09ST02IEC","M3STG09ST02MDP","M3STG09ST02PS")$RestoreCommands = @()foreach($db in $dbs) {    $FullFile = Get-ChildItem -path E:\backups01\full\$db | Sort-Object LastWriteTime | Select-Object -Last 1    $DiffFile = Get-ChildItem -path E:\backups01\diff\$db | Sort-Object LastWriteTime | Select-Object -Last 1    $FullPath = $FullFile.FullName    $DiffPath = $DiffFile.FullName    $FullQuery = "RESTORE DATABASE [$db] FROM DISK = '$FullPath' WITH FILE = 1, NORECOVERY;"    $DiffQuery = "RESTORE DATABASE [$db] FROM DISK = '$DiffPath' WITH FILE = 1, RECOVERY;"    $RestoreCommands += $FullQuery    $RestoreCommands += $DiffQuery    $RestoreCommands | Out-File -FilePath C:\restore.txt}